import "./App.css";
import { useState } from "react";

function App() {

  const [ name , setName ] = new useState("");

  function entering( event ){
    setName( event.target.value )
  }


  return (
    <div className="App">

      <h1> Hello ! {name}</h1>

      <input 
      type="text"
      placeholder="Enter your name !"
      onChange={entering}  />

      <button>submit</button>


    </div>
  );
}
export default App;

