import "./App.css";
import { useState } from "react";

function App() {

  const [ enteredName, setEnteredName ] = new useState("");
  const [ name , setName ] = new useState("");


  function entering( event ){
    setEnteredName( event.target.value )
  }

  function onSubmit(){
    setName( enteredName )
  }

  return (
    <div className="App">
      <h1> Hello ! {name}</h1>

      <input
        type="text"
        placeholder="Enter your name !"
        onChange={entering}
        value={enteredName}
      />
      {/* value attribute me jo bhi enteredName me value ayi h wo store hoga. onChange  */}

      <button onClick={onSubmit}>submit</button>
    </div>
  );
}
export default App;

