import "./App.css";
import { useState } from "react";

function App() {

  const [fullname, setfullname] = new useState(
    {
        fname:"",
        lname:""
    }
  );

  function input( e ) {

    const value = e.target.value;

    const name = e.target.name;

    setfullname( (previousvalue )=>{

      if( name==="fname"){
        return{
          fname:value , 
          lname:previousvalue.lname 
        }
      }
      else if( name=="lname") {
        return{
          fname:previousvalue.fname , 
          lname:value 
        }
      }
      else if( name="mail")
      {
        return {
          fname: previousvalue.fname,
          lname: value,
        };
      }
    })

  }

  function onSubmit(e) 
  { 
    e.preventDefault();
    alert("Form submitted !! ")

  }


  return (
    <div className="App">
      <form onSubmit={onSubmit}>
        <h1>
          Hello ! {fullname.fname} {fullname.lname}
          <div> Your email is : {info.mail}</div>
          <div> Your mob no. is : {info.no}</div>
        </h1>

        <input
          type="text"
          placeholder="Enter your first name !"
          name="fname"
          onChange={input}
          value={info.fname}
        />

        <input
          type="text"
          placeholder="Enter your Lastname !"
          name="lname"
          onChange={input}
          value={info.lname}
        />

        <input
          type="email"
          placeholder="Enter your email !"
          name="mail"
          onChange={input}
          value={info.mail}
        />

        <input
          type="text"
          placeholder="Enter your mobile no !"
          name="number"
          onChange={input}
          value={info.no}
        />

        <button onClick={onSubmit}>submit</button>
      </form>
    </div>
  );
}
export default App;
