import "./App.css";
import { useState } from "react";

function App() {

  const color = 'red';
  const [bg, setBg] = useState( color );

  function ChangeBg() {
    let newtext = "ekbar click kiya hai";
    setBg(newtext);
  }

  return (
    <div className="App">

      <button style={{backgroundColor : bg}} onMouseEnter={ChangeBg}> {text} </button>

    </div>
  );
}



export default App;
