import "./App.css";
import { useState } from "react";

function App() {

  const [info, setInfo] = new useState(
    {
        fname:"",
        lname:"",
        mail:"",
        number:""
    }
  );

  function input( e ) {

    const value = e.target.value;

    const name = e.target.name;

    setInfo((previousvalue) => {
      if (name === "fname") {
        return {
          fname: value,
          lname: previousvalue.lname,
          mail: previousvalue.mail,
          number: previousvalue.number
        };
      } else if (name === "lname") {
        return {
          fname: previousvalue.fname,
          lname: value,
          mail: previousvalue.mail,
          number: previousvalue.number,
        };
      } else if ((name === "mail")) {
        return {
          fname: previousvalue.fname,
          lname: previousvalue.lname,
          mail: value,
          number: previousvalue.number
        };
      }
      else{
        return {
          fname: previousvalue.fname,
          lname: previousvalue.lname,
          mail: previousvalue.mail,
          number: value
        };
      }
    });

  }

  function onSubmit(e) 
  { 
    e.preventDefault();
    alert("Form submitted !! ")

  }


  return (
    <div className="App">
      <form onSubmit={onSubmit}>
        <h1>
          Hello ! {info.fname} {info.lname}
          <div> Your email is : {info.mail}</div>
          <div> Your mob no. is : {info.no}</div>
        </h1>

        <input
          type="text"
          placeholder="Enter your first name !"
          name="fname"
          onChange={input}
          value={info.fname}
        />

        <input
          type="text"
          placeholder="Enter your Lastname !"
          name="lname"
          onChange={input}
          value={info.lname}
        />

        <input
          type="email"
          placeholder="Enter your email !"
          name="mail"
          onChange={input}
          value={info.mail}
        />

        <input
          type="number"
          placeholder="Enter your mobile no !"
          name="number"
          onChange={input}
          value={info.no}
        />

        <button onClick={onSubmit}>submit</button>
      </form>
    </div>
  );
}
export default App;
